mapper.select:
SELECT a.rule
FROM qyfinance.RebateAgency a
WHERE a.id = #{param}

SELECT a.id,a.customerId,a.cycle,a.apiTypeId,a.stid,a.apiId,a.costCount,a.cost,a.price, a.rebateBegPrice,a.rebateEndPrice,a.companyName, CONCAT_WS('-',b.name,c.name) type_stid_name, e.name vendorName FROM ( SELECT a.id,a.customerId,a.cycle,a.apiTypeId,a.stid,a.apiId,a.costCount,a.cost,a.price, a.rebateBegPrice,a.rebateEndPrice,b.companyId,d.name companyName FROM qyfinance.RebateCustomerBill a LEFT JOIN qyfinance.finance_Customer b ON a.customerId = b.id LEFT JOIN qyfinance.finance_agency_company c ON b.companyId = c.companyId LEFT JOIN qyfinance.bkvwCustomerCompany d ON b.companyId = d.id
AND c.agencyId = #{agencyId}
AND a.cycle IN
#{cyc}
AND b.companyId IN
#{cid}
AND a.apiTypeId IN
#{tid}
AND a.stid IN
#{stid}
) a LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId = b.id LEFT JOIN qyfinance.bkvwMobileOperator c ON a.stid = c.id LEFT JOIN qyfinance.finance_Api d ON a.apiId = d.id LEFT JOIN qyfinance.finance_ApiVendor e ON d.vendorId = e.id
AND e.partnerId <> #{agencyId} OR e.partnerId IS NULL

SELECT a.cycle,a.apiTypeId,a.stid,a.apiId,a.costCount,a.cost, CONCAT_WS('-',b.name,c.name) type_stid_name, e.name vendorName FROM ( SELECT a.cycle,a.apiTypeId,a.stid,a.apiId,a.cost,sum(a.costCount) costCount FROM qyfinance.RebateCustomerBill a LEFT JOIN qyfinance.finance_Customer b ON a.customerId = b.id LEFT JOIN qyfinance.finance_agency_company c ON b.companyId = c.companyId LEFT JOIN qyfinance.bkvwCustomerCompany d ON b.companyId = d.id
AND c.agencyId <> #{agencyId} OR c.agencyId IS NULL
GROUP BY a.cycle,a.apiTypeId,a.stid,a.apiId,a.cost ) a LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId = b.id LEFT JOIN qyfinance.bkvwMobileOperator c ON a.stid = c.id LEFT JOIN qyfinance.finance_Api d ON a.apiId = d.id LEFT JOIN qyfinance.finance_ApiVendor e ON d.vendorId = e.id
AND e.partnerId = #{agencyId}
AND a.cycle IN
#{cyc}

SELECT a.id,a.customerId,a.cycle,a.apiTypeId,a.stid,a.price,a.costCount,a.companyName, CONCAT_WS('-',b.name,c.name) type_stid_name FROM ( SELECT a.id,a.customerId,a.cyc cycle,a.apiTypeId,a.stid,a.price,a.count costCount,b.companyId, d.name companyName FROM qyfinance.CustomerCacheMonthUser a LEFT JOIN qyfinance.finance_Customer b ON a.customerId = b.id LEFT JOIN qyfinance.finance_agency_company c ON b.companyId = c.companyId LEFT JOIN qyfinance.bkvwCustomerCompany d ON b.companyId = d.id
AND c.agencyId = #{agencyId}
) a LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId = b.id LEFT JOIN qyfinance.bkvwMobileOperator c ON a.stid = c.id
AND a.cycle IN
#{cyc}
AND a.companyId IN
#{cid}
AND a.apiTypeId IN
#{tid}
AND a.stid IN
#{stid}

mapper.update:
UPDATE qyfinance.RebateCustomerBill
SET costCount = #{param2}
WHERE id = #{param1}

UPDATE qyfinance.RebateCustomerBill
SET cost = #{param2}
WHERE id = #{param1}

UPDATE qyfinance.RebateCustomerBill
SET price = #{param2}
WHERE id = #{param1}

UPDATE qyfinance.RebateCustomerBill
SET rebateBegPrice = #{param2}
WHERE id = #{param1}

UPDATE qyfinance.RebateCustomerBill
SET rebateEndPrice = #{param2}
WHERE id = #{param1}

