mapper.select:
SELECT ee.id apiId,ee.apiTypeId,ee.apiTypeName,ee.vendorId,ee.vendorName,ee.apiName,ee.balance,ee.weekTotleCost,ee.monthTotleCost, ff.consumeTotleAmount FROM ( SELECT cc.id,cc.apiTypeId,cc.apiTypeName,cc.vendorId,cc.vendorName,cc.apiName,cc.balance,cc.weekTotleCost,dd.monthTotleCost FROM ( SELECT aa.id,aa.apiTypeId,aa.apiTypeName,aa.vendorId,aa.vendorName,aa.apiName,aa.balance,bb.weekTotleCost FROM ( SELECT a.id,a.apiTypeId,b.name apiTypeName,a.vendorId,a.vendorName,a.name apiName,c.balance FROM qyfinance.bkvwApi a LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId=b.id LEFT JOIN qyfinance.bkvwApiBalance c ON a.id=c.apiId ) aa LEFT JOIN ( SELECT apiId,totleCost weekTotleCost FROM qyfinance.bkvwApiWeekMonthAmount WHERE typeId=1 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%x') AND weeks=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%v') ) bb ON aa.id=bb.apiId ) cc LEFT JOIN ( SELECT apiId,totleCost monthTotleCost FROM qyfinance.bkvwApiWeekMonthAmount WHERE typeId=2 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%m') ) dd ON cc.id=dd.apiId ) ee LEFT JOIN ( SELECT SUM(cost) consumeTotleAmount,apiId FROM qyfinance.bkvwApiRequestLog WHERE ok=1 GROUP BY apiId ) ff ON ee.id=ff.apiId
ee.id=#{apiId}
AND ee.apiTypeId=#{apiTypeId}
AND ee.vendorId=#{vendorId}

SELECT a.id apiId,a.apiTypeId,b.name apiTypeName,a.vendorId,a.vendorName,a.name apiName,c.balance
FROM qyfinance.bkvwApi a LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId=b.id LEFT JOIN qyfinance.bkvwApiBalance c
ON a.id=c.apiId


        a.apiTypeId=#{apiTypeId}


        AND a.vendorId=#{vendorId}

SELECT a.customerId,a.apiId,a.cost,a.ok,a.resTime,a.createTime,b.companyName FROM qyfinance.bkvwApiRequestLog a LEFT JOIN qyfinance.bkvwCustomer b ON a.customerId=b.id WHERE a.ok=1 AND a.apiId=#{apiId}
AND a.createTime>=str_to_date(#{beginDate},'%m/%d/%Y %H:%i:%s')
AND a.createTime<=str_to_date(#{endDate},'%m/%d/%Y %H:%i:%s')
