mapper.select:
SELECT aa.id apiId,aa.apiTypeId,aa.apiTypeName,aa.vendorId,aa.vendorName,aa.apiName,aa.mobileOperatorId, aa.mobileOperatorName,aa.status,bb.weekTotleCost,cc.monthTotleCost,dd.consumeTotleAmount FROM ( SELECT a.id,a.apiTypeId,b.name apiTypeName,a.vendorId,a.vendorName,a.name apiName,a.status, c.mobileOperatorId,c.name mobileOperatorName FROM qyfinance.bkvwApi a LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId=b.id LEFT JOIN qyfinance.bkvwApiMobileOperator c ON a.id=c.apiId ) aa LEFT JOIN ( SELECT apiId,totleCost weekTotleCost FROM qyfinance.bkvwApiWeekMonthAmount WHERE typeId=1 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%x') AND weeks=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%v') ) bb ON aa.id=bb.apiId LEFT JOIN ( SELECT apiId,totleCost monthTotleCost FROM qyfinance.bkvwApiWeekMonthAmount WHERE typeId=2 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%m') ) cc ON aa.id=cc.apiId LEFT JOIN ( SELECT sum(cost) consumeTotleAmount,apiId FROM qyfinance.bkvwApiRequestLog WHERE ok=1 GROUP BY apiId ) dd ON aa.id=dd.apiId
aa.status=#{status}
AND aa.vendorId=#{vendorId}
AND aa.apiTypeId=#{apiTypeId}

SELECT sum(bb.weekTotleCost)
FROM qyfinance.bkvwApi aa
LEFT JOIN
(
    SELECT apiId,totleCost weekTotleCost
    FROM qyfinance.bkvwApiWeekMonthAmount
    WHERE typeId=1 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%x')
    AND weeks=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%v')
) bb ON aa.id=bb.apiId


        aa.status=#{status}


        AND aa.vendorId=#{vendorId}


        AND aa.apiTypeId=#{apiTypeId}

SELECT sum(cc.monthTotleCost)
FROM qyfinance.bkvwApi aa
LEFT JOIN
(
    SELECT apiId,totleCost monthTotleCost
    FROM qyfinance.bkvwApiWeekMonthAmount
    WHERE typeId=2 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%Y')
    AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%m')
) cc ON aa.id=cc.apiId


        aa.status=#{status}


        AND aa.vendorId=#{vendorId}


        AND aa.apiTypeId=#{apiTypeId}

SELECT sum(dd.consumeTotleAmount)
FROM qyfinance.bkvwApi aa
LEFT JOIN
(
SELECT sum(cost) consumeTotleAmount,apiId
FROM qyfinance.bkvwApiRequestLog
WHERE ok=1
GROUP BY apiId
) dd ON aa.id=dd.apiId


        aa.status=#{status}


        AND aa.vendorId=#{vendorId}


        AND aa.apiTypeId=#{apiTypeId}

SELECT distinct(vendorId) id,vendorName name
FROM qyfinance.bkvwApi


        AND apiTypeId=#{apiTypeId}

SELECT a.customerId,a.apiId,a.cost,a.ok,a.resTime,a.createTime,b.companyName FROM qyfinance.bkvwApiRequestLog a LEFT JOIN qyfinance.bkvwCustomer b ON a.customerId=b.id WHERE a.ok=1 AND a.apiId=#{apiId}
AND a.createTime>=str_to_date(#{beginDate},'%Y/%m/%d %H:%i:%s')
AND a.createTime<=str_to_date(#{endDate},'%Y/%m/%d %H:%i:%s')

SELECT sum(a.cost) FROM qyfinance.bkvwApiRequestLog a WHERE a.ok=1 AND a.apiId=#{apiId}
AND a.createTime>=str_to_date(#{beginDate},'%Y/%m/%d %H:%i:%s')
AND a.createTime<=str_to_date(#{endDate},'%Y/%m/%d %H:%i:%s')

SELECT ee.vendorId,ee.vendorName,hh.status apiStatus,ee.weekTotleCost,ee.monthTotleCost,ee.consumeTotleAmount, ff.balance,gg.partnerId,gg.partnerName,gg.status apiVendorStatus,hh.apiTypeId,ii.name mobileOperatorName,jj.name apiTypeName FROM ( SELECT aa.vendorId,aa.vendorName,sum(bb.weekTotleCost) weekTotleCost,sum(cc.monthTotleCost) monthTotleCost, sum(dd.consumeTotleAmount) consumeTotleAmount FROM qyfinance.bkvwApi aa LEFT JOIN ( SELECT apiId,totleCost weekTotleCost FROM qyfinance.bkvwApiWeekMonthAmount WHERE typeId=1 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%x') AND weeks=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%v') ) bb ON aa.id=bb.apiId LEFT JOIN ( SELECT apiId,totleCost monthTotleCost FROM qyfinance.bkvwApiWeekMonthAmount WHERE typeId=2 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%m') ) cc ON aa.id=cc.apiId LEFT JOIN ( SELECT sum(cost) consumeTotleAmount,apiId FROM qyfinance.bkvwApiRequestLog WHERE ok=1 GROUP BY apiId ) dd ON aa.id=dd.apiId GROUP BY aa.vendorId,aa.vendorName ) ee LEFT JOIN qyfinance.bkvwApiVendorBalance ff ON ee.vendorId=ff.vendorId LEFT JOIN qyfinance.bkvwApiVendor gg ON ee.vendorId=gg.vendorId LEFT JOIN qyfinance.bkvwApi hh ON ee.vendorId=hh.vendorId LEFT JOIN qyfinance.bkvwApiMobileOperator ii ON hh.id=ii.apiId LEFT JOIN qyfinance.bkvwApiType jj ON hh.apiTypeId=jj.id
AND ee.vendorId=#{vendorId}
AND gg.partnerId=#{partnerId}

SELECT sum(ee.weekTotleCost) FROM ( SELECT aa.vendorId,aa.vendorName,sum(bb.weekTotleCost) weekTotleCost FROM qyfinance.bkvwApi aa LEFT JOIN ( SELECT apiId,totleCost weekTotleCost FROM qyfinance.bkvwApiWeekMonthAmount WHERE typeId=1 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%x') AND weeks=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%v') ) bb ON aa.id=bb.apiId GROUP BY aa.vendorId,aa.vendorName ) ee LEFT JOIN qyfinance.bkvwApiVendor gg ON ee.vendorId=gg.vendorId
AND ee.vendorId=#{vendorId}
AND gg.partnerId=#{partnerId}

SELECT sum(ee.monthTotleCost) FROM ( SELECT aa.vendorId,aa.vendorName,sum(cc.monthTotleCost) monthTotleCost FROM qyfinance.bkvwApi aa LEFT JOIN ( SELECT apiId,totleCost monthTotleCost FROM qyfinance.bkvwApiWeekMonthAmount WHERE typeId=2 AND tableId=3 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%m') ) cc ON aa.id=cc.apiId GROUP BY aa.vendorId,aa.vendorName ) ee LEFT JOIN qyfinance.bkvwApiVendor gg ON ee.vendorId=gg.vendorId
AND ee.vendorId=#{vendorId}
AND gg.partnerId=#{partnerId}

SELECT sum(ee.consumeTotleAmount) FROM ( SELECT aa.vendorId,aa.vendorName,sum(dd.consumeTotleAmount) consumeTotleAmount FROM qyfinance.bkvwApi aa LEFT JOIN ( SELECT sum(cost) consumeTotleAmount,apiId FROM qyfinance.bkvwApiRequestLog WHERE ok=1 GROUP BY apiId ) dd ON aa.id=dd.apiId GROUP BY aa.vendorId,aa.vendorName ) ee LEFT JOIN qyfinance.bkvwApiVendor gg ON ee.vendorId=gg.vendorId
AND ee.vendorId=#{vendorId}
AND gg.partnerId=#{partnerId}
