SELECT cc.customerId,cc.typeId,cc.typeName,cc.authId,cc.authPass,cc.balance,cc.createTime customerCreateTime,cc.status,cc.statusName, dd.companyId,dd.companyBalance,dd.companyName,dd.partnerId,dd.partnerName,dd.timestamp companyCreateTime FROM ( SELECT a.id customerId,a.typeId,a.typeName,a.authId,a.authPass,a.balance,a.createTime,a.status,a.statusName, a.companyId,a.companyName,a.partnerId,b.name partnerName FROM qyfinance.bkvwCustomer a LEFT JOIN qyfinance.bkvwPartner b ON a.partnerId=b.id ) cc LEFT JOIN ( SELECT aa.companyId,aa.companyBalance,bb.companyName,bb.partnerId,bb.partnerName,bb.timestamp FROM ( SELECT companyId,sum(balance) companyBalance FROM qyfinance.bkvwCustomer WHERE typeId=1 AND status=0 GROUP BY companyId ) aa LEFT JOIN qyfinance.bkvw_company_customer bb ON aa.companyId=bb.companyId ) dd ON cc.companyId=dd.companyId
cc.partnerId=#{partnerId}
AND dd.companyName like '%${content}%'

SELECT cc.customerId,cc.typeId,cc.typeName,cc.authId,cc.authPass,cc.balance,cc.createTime customerCreateTime,cc.status,cc.statusName, dd.companyId,dd.companyBalance,dd.companyName,dd.partnerId,dd.partnerName,dd.timestamp companyCreateTime FROM ( SELECT a.id customerId,a.typeId,a.typeName,a.authId,a.authPass,a.balance,a.createTime,a.status,a.statusName, a.companyId,a.companyName,a.partnerId,b.name partnerName FROM qyfinance.bkvwCustomer a LEFT JOIN qyfinance.bkvwPartner b ON a.partnerId=b.id WHERE a.id IN ( SELECT customerId FROM qyfinance.bkvwCustomerDept WHERE deptId IN
#{deptId}
) ) cc LEFT JOIN ( SELECT aa.companyId,aa.companyBalance,bb.companyName,bb.partnerId,bb.partnerName,bb.timestamp FROM ( SELECT companyId,sum(balance) companyBalance FROM qyfinance.bkvwCustomer WHERE typeId=1 AND status=0 GROUP BY companyId ) aa LEFT JOIN qyfinance.bkvw_company_customer bb ON aa.companyId=bb.companyId ) dd ON cc.companyId=dd.companyId
cc.partnerId=#{partnerId}
AND dd.companyName like '%${content}%'

