SELECT qc.id,qc.typeId,qc.name,qc.authId,qc.authPass,qc.balance,qc.createTime,qc.status,qct.name qctname,qcs.name qcsname FROM qydata.Customer qc,qydata.CustomerType qct,qydata.CustomerStatus qcs WHERE qc.typeId=qct.id AND qc.status=qcs.id AND qc.companyId=#{companyId}
AND (qc.name LIKE '%${content}%' OR qc.authId LIKE '%${content}%' OR qcs.name LIKE '%${content}%' OR qct.name LIKE '%${content}%')
ORDER BY qc.createTime DESC limit #{beginIndex},#{lineSize}

SELECT DISTINCT (a.apiId),a.price,a.enabled,a.apiTypeId, a.vendorId,a.apiName,apiType.name apiTypeName,customer.companyId FROM ( SELECT customerApi.id customerApiId,customerApi.apiId,customerApi.customerId,customerApi.price, customerApi.enabled,customerApi.createTime,api.apiTypeId,api.vendorId,api.name apiName FROM qydata.CustomerApi customerApi,qydata.Api api WHERE customerApi.apiId=api.id AND customerId IN
#{customerId}
) a,qydata.ApiType apiType,qydata.Customer customer WHERE a.apiTypeId=apiType.id AND a.customerId=customer.id AND apiType.name NOT LIKE '%mobile%' ORDER BY a.createTime DESC limit #{beginIndex},#{lineSize}

SELECT DISTINCT (b.apiId),b.price,b.enabled,b.apiTypeId, b.vendorId,b.apiName,b.apiTypeName,b.companyId,mobileOperator.name mobileOperatorName FROM ( SELECT a.customerApiId,a.apiId,a.customerId,a.price,a.enabled,a.createTime,a.apiTypeId, a.vendorId,a.apiName,apiType.name apiTypeName,customer.companyId FROM ( SELECT customerApi.id customerApiId,customerApi.apiId,customerApi.customerId,customerApi.price, customerApi.enabled,customerApi.createTime,api.apiTypeId,api.vendorId,api.name apiName FROM qydata.CustomerApi customerApi,qydata.Api api WHERE customerApi.apiId=api.id AND customerId IN
#{customerId}
) a,qydata.ApiType apiType,qydata.Customer customer WHERE a.apiTypeId=apiType.id AND a.customerId=customer.id AND apiType.name LIKE '%mobile%' ) b,qydata.ApiMobileOperator apiMobileOperator,qydata.MobileOperator mobileOperator WHERE b.apiId=apiMobileOperator.apiId AND apiMobileOperator.mobileOperatorId=mobileOperator.id ORDER BY b.createTime DESC limit #{beginIndex},#{lineSize}

