#{customerId}
) a,qydata.ApiType apiType,qydata.Customer customer WHERE a.apiTypeId=apiType.id AND a.customerId=customer.id AND apiType.name NOT LIKE '%mobile%' UNION SELECT DISTINCT (b.apiId),b.price,b.enabled,b.apiTypeId, b.vendorId,b.apiName,b.apiTypeName,b.companyId FROM ( SELECT a.customerApiId,a.apiId,a.customerId,a.price,a.enabled,a.createTime,a.apiTypeId, a.vendorId,a.apiName,apiType.name apiTypeName,customer.companyId FROM ( SELECT customerApi.id customerApiId,customerApi.apiId,customerApi.customerId,customerApi.price, customerApi.enabled,customerApi.createTime,api.apiTypeId,api.vendorId,api.name apiName FROM qydata.CustomerApi customerApi,qydata.Api api WHERE customerApi.apiId=api.id AND customerId IN
#{customerId}
) a,qydata.ApiType apiType,qydata.Customer customer WHERE a.apiTypeId=apiType.id AND a.customerId=customer.id AND apiType.name LIKE '%mobile%' ) b,qydata.ApiMobileOperator apiMobileOperator,qydata.MobileOperator mobileOperator WHERE b.apiId=apiMobileOperator.apiId AND apiMobileOperator.mobileOperatorId=mobileOperator.id ) result

SELECT result.apiTypeName,result.companyId,result.authId,result.apiId,result.customerId,result.price, result.enabled,result.createTime,mobileOperator.name mobileOperatorName FROM ( SELECT casual.apiTypeName,casual.companyId,casual.authId,casual.apiId,casual.customerId,casual.price, casual.enabled,casual.createTime,apiMobileOperator.mobileOperatorId FROM ( SELECT apiType.name apiTypeName,customer.companyId,customer.authId,customerApiAndApi.apiId,customerApiAndApi.customerId,customerApiAndApi.price, customerApiAndApi.enabled,customerApiAndApi.createTime FROM qydata.ApiType apiType,qydata.Customer customer,( SELECT customerApi.apiId,customerApi.customerId,customerApi.price, customerApi.enabled,customerApi.createTime,api.apiTypeId FROM qydata.CustomerApi customerApi,qydata.Api api WHERE customerApi.apiId=api.id AND customerApi.customerId IN
#{customerId}
) customerApiAndApi WHERE customerApiAndApi.apiTypeId=apiType.id AND customerApiAndApi.customerId=customer.id ) casual LEFT JOIN qydata.ApiMobileOperator apiMobileOperator ON casual.apiId=apiMobileOperator.apiId ) result LEFT JOIN qydata.MobileOperator mobileOperator ON result.mobileOperatorId=mobileOperator.id ORDER BY result.createTime limit #{beginIndex},#{lineSize}

