mapper.select:
SELECT id,name,createTime
FROM qydata.Company
WHERE id=#{param}

SELECT id,name,createTime
FROM qydata.Company
WHERE id IN (
                SELECT companyId
                FROM qydata.Customer
                WHERE id IN (
                                SELECT customerId
                                FROM qyauth.CustomerDept customerDept


                                        deptId=#{deptId}


                              )
             )
limit #{beginIndex},#{lineSize}

SELECT count(id) FROM qydata.Company WHERE id IN ( SELECT companyId FROM qydata.Customer WHERE id IN ( SELECT customerId FROM qyauth.CustomerDept customerDept
deptId=#{deptId}
) )

SELECT id,companyId
FROM qydata.Customer
WHERE companyId=#{param}

SELECT a.id apiId,a.apiTypeId,a.vendorId,a.cost,a.apiName,a.apiTypeName FROM ( SELECT api.id,api.apiTypeId,api.vendorId,api.cost,api.name apiName,apiType.name apiTypeName FROM qydata.Api api,qydata.ApiType apiType WHERE api.apiTypeId=apiType.id AND apiType.id IN ( SELECT id FROM qydata.ApiType WHERE name NOT LIKE '%mobile%' ) ) a,( SELECT api.id,MIN(api.cost) FROM qydata.Api api GROUP BY api.apiTypeId ) b WHERE a.id=b.id
and a.id not in( select apiId from qydata.CustomerApi where customerId IN
#{customerId}
)

SELECT a.apiTypeName,a.apiId,a.apiTypeId,a.vendorId,a.apiName,a.cost,a.mobileOperatorName,a.mobileOperatorId FROM ( SELECT qat.name apiTypeName,qaam.apiId,qaam.apiTypeId,qaam.vendorId,qaam.apiName,qaam.cost,qaam.mobileOperatorName,qaam.mobileOperatorId FROM qydata.ApiType qat,( SELECT qa.id apiId,qa.apiTypeId,qa.vendorId,qa.name apiName,qa.cost,qmo.name mobileOperatorName,qmo.id mobileOperatorId FROM qydata.ApiMobileOperator qamo,qydata.Api qa,qydata.MobileOperator qmo WHERE qamo.apiId=qa.id and qamo.mobileOperatorId=qmo.id ) qaam WHERE qat.id=qaam.apiTypeId ) a,( SELECT api.id,MIN(api.cost) FROM qydata.Api api GROUP BY api.apiTypeId ) b WHERE a.apiId=b.id
and a.apiId not in( select apiId from qydata.CustomerApi where customerId IN
#{customerId}
)

SELECT a.customerApiId,a.apiId,a.customerId,a.price,a.enabled,a.createTime,a.apiTypeId, a.vendorId,a.apiName,apiType.name apiTypeName FROM ( SELECT customerApi.id customerApiId,customerApi.apiId,customerApi.customerId,customerApi.price, customerApi.enabled,customerApi.createTime,api.apiTypeId,api.vendorId,api.name apiName FROM qydata.CustomerApi customerApi,qydata.Api api WHERE customerApi.apiId=api.id AND customerId IN
#{customerId}
) a,qydata.ApiType apiType WHERE a.apiTypeId=apiType.id AND apiType.name NOT LIKE '%mobile%' ORDER BY a.createTime DESC limit #{beginIndex},#{lineSize}

SELECT b.customerApiId,b.apiId,b.customerId,b.price,b.enabled,b.createTime,b.apiTypeId, b.vendorId,b.apiName,b.apiTypeName,mobileOperator.name mobileOperatorName FROM ( SELECT a.customerApiId,a.apiId,a.customerId,a.price,a.enabled,a.createTime,a.apiTypeId, a.vendorId,a.apiName,apiType.name apiTypeName FROM ( SELECT customerApi.id customerApiId,customerApi.apiId,customerApi.customerId,customerApi.price, customerApi.enabled,customerApi.createTime,api.apiTypeId,api.vendorId,api.name apiName FROM qydata.CustomerApi customerApi,qydata.Api api WHERE customerApi.apiId=api.id AND customerId IN
#{customerId}
) a,qydata.ApiType apiType WHERE a.apiTypeId=apiType.id AND apiType.name LIKE '%mobile%' ) b,qydata.ApiMobileOperator apiMobileOperator,qydata.MobileOperator mobileOperator WHERE b.apiId=apiMobileOperator.apiId AND apiMobileOperator.mobileOperatorId=mobileOperator.id ORDER BY b.createTime DESC limit #{beginIndex},#{lineSize}
