mapper.select:
SELECT aa.id customerId,aa.companyId,aa.companyName,bb.id partnerId,bb.name partnerName FROM qyfinance.bkvwCustomer aa LEFT JOIN qyfinance.bkvwPartner bb ON aa.partnerId = bb.id WHERE aa.typeId = 1 AND aa.companyId IS NOT NULL
AND aa.companyName LIKE '%${companyName}%'
AND aa.partnerId = #{partnerId}

SELECT a.customerId,sum(a.costCount) costCount,sum(a.costUpCount) costUpCount
FROM qyfinance.CompanyApiTypeConsumeDayCountCache a


        a.consuTime >= #{beginDate}



          AND a.consuTime < #{endDate}



GROUP BY a.customerId

SELECT aa.customerId,aa.apiTypeId,aa.stid,aa.costCount,aa.costUpCount,(aa.costCount-aa.costUpCount) cacheCount, bb.name apiTypeName,cc.name stidName,CONCAT_WS('--',bb.name,cc.name) apiTypeName_stidName FROM ( SELECT a.customerId,a.apiTypeId,a.stid,sum(a.costCount) costCount,sum(a.costUpCount) costUpCount FROM qyfinance.CompanyApiTypeConsumeDayCountCache a
a.consuTime >= #{beginDate}
AND a.consuTime < #{endDate}
GROUP BY a.customerId,a.apiTypeId,a.stid ) aa LEFT JOIN qyfinance.bkvwApiType bb ON aa.apiTypeId = bb.id LEFT JOIN qyfinance.bkvwMobileOperator cc ON aa.stid = cc.id WHERE (aa.costCount-aa.costUpCount) > 0
