SELECT a.id,a.balance,a.companyId,a.companyName,a.partnerId,b.name partnerName,a.floor,a.companyStatus FROM qyfinance.bkvwCustomer a LEFT JOIN qyfinance.bkvwPartner b ON a.partnerId=b.id LEFT JOIN qyfinance.bkvwCustomerDept c ON a.id=c.customerId WHERE a.typeId = 1 AND a.companyId IS NOT NULL
and c.deptId in
#{deptId}
and a.companyStatus in
#{status}
and a.partnerId=#{partnerId}
and a.companyName like '%${content}%'

SELECT companyName
FROM qyfinance.bkvwCustomer
WHERE id=#{param}

SELECT base.id customerId, aa.apiTypeId, aa.stid subTypeId, ifnull(aa.sumAmount,0) sumAmount, ifnull(aa.countTotle,0) countTotle, ifnull(aa.countSuccess,0) countSuccess, ifnull(bb.sumAmount,0) currDaySumAmount, ifnull(bb.countTotle,0) currDayCountTotle, ifnull(bb.countSuccess,0) currDayCountSuccess, cc.companyId, dd.price, dd.enabled, ee.name apiTypeName, ff.name subTypeName FROM qyfinance.bkvwCustomer base LEFT JOIN ( SELECT customerId, apiTypeId, ifnull(stid,0) stid, sum(sumAmount) sumAmount, sum(countTotle) countTotle, sum(countSuccess) countSuccess FROM qyfinance.bkvwCompanyApiTypeConsumeDayCount
AND consuTime >= #{beginDate}
AND consuTime < #{endDate}
GROUP BY customerId,apiTypeId,stid ) aa ON base.id = aa.customerId LEFT JOIN ( SELECT a.customerId, a.apiTypeId, ifnull(a.stid,0) stid, sum(b.amount) sumAmount, count(a.id) countTotle, count(b.id) countSuccess FROM qyfinance.finance_CustomerRequestLog a LEFT JOIN qyfinance.finance_CustomerBalanceLog b ON a.id = b.reqId WHERE a.createTime >= #{currDayTime} GROUP BY a.customerId,a.apiTypeId,a.stid ) bb ON aa.customerId = bb.customerId AND aa.apiTypeId = bb.apiTypeId AND aa.stid = bb.stid LEFT JOIN qyfinance.finance_Customer cc ON aa.customerId = cc.id LEFT JOIN qyfinance.bkvwCompanyApi dd ON cc.companyId=dd.companyId AND aa.apiTypeId=dd.apiTypeId AND aa.stid=dd.subTypeId LEFT JOIN qyfinance.bkvwApiType ee ON aa.apiTypeId=ee.id LEFT JOIN qyfinance.bkvwMobileOperator ff ON aa.stid=ff.id WHERE base.typeId = 1 ORDER BY ee.name ASC,dd.enabled DESC

SELECT customerId id,sum(sumAmount) consumeTotleAmount
FROM qyfinance.bkvwCompanyApiTypeConsumeDayCount


          consuTime >= #{beginDate}



            AND consuTime < #{endDate}



GROUP BY customerId

SELECT id companyId,email
FROM qyfinance.excel_CustomerCompanyEmail


        id = #{companyId}

SELECT year,month,customerId,excelCode,consuTime
FROM qyfinance.excel_CustomerConsumeExcel
WHERE year = #{years} AND month = #{months}

