mapper.select:
SELECT kk.id,kk.companyId,kk.companyName,kk.balance,kk.chargeWeekTotleAmount,kk.chargeMonthTotleAmount,kk.consumeWeekTotleAmount, kk.consumeMonthTotleAmount,kk.chargeTotleAmount,ll.consumeTotleAmount FROM (SELECT ii.id,ii.companyId,ii.companyName,ii.balance,ii.chargeWeekTotleAmount,ii.chargeMonthTotleAmount,ii.consumeWeekTotleAmount, ii.consumeMonthTotleAmount,jj.chargeTotleAmount FROM (SELECT gg.id,gg.companyId,gg.companyName,gg.balance,gg.chargeWeekTotleAmount,gg.chargeMonthTotleAmount,gg.consumeWeekTotleAmount, hh.consumeMonthTotleAmount FROM (SELECT ee.id,ee.companyId,ee.companyName,ee.balance,ee.chargeWeekTotleAmount,ee.chargeMonthTotleAmount,ff.consumeWeekTotleAmount FROM (SELECT cc.id,cc.companyId,cc.companyName,cc.balance,cc.chargeWeekTotleAmount,dd.chargeMonthTotleAmount FROM (SELECT aa.id,aa.typeId,aa.typeName,aa.name,aa.authId,aa.authPass,aa.createTime,aa.status,aa.statusName,aa.companyId,aa.companyName,aa.balance,bb.chargeWeekTotleAmount FROM (SELECT a.id,a.typeId,a.typeName,a.name,a.authId,a.authPass,a.balance,a.createTime,a.status,a.statusName,a.companyId,a.companyName, b.deptId,b.deptName FROM qydata.vwCustomer a,qyauth.vwCustomerDept b WHERE a.id=b.customerId
and b.deptId in
#{deptId}
and a.typeId in
#{customerTypeId}
and a.companyName like '%${content}%'
) aa LEFT JOIN (SELECT customerId,totleAmount chargeWeekTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE weekMonthTypeId=1 AND tableName='CustomerBalanceLog' AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%m') AND weeks=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%u') ) bb ON aa.id=bb.customerId ) cc LEFT JOIN (SELECT customerId,totleAmount chargeMonthTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE weekMonthTypeId=2 AND tableName='CustomerBalanceLog' AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%m') ) dd ON cc.id=dd.customerId ) ee LEFT JOIN (SELECT customerId,totleAmount consumeWeekTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE weekMonthTypeId=1 AND tableName='CustomerRequestLogCustomerResponseLog' AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%m') AND weeks=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%u') ) ff ON ee.id=ff.customerId ) gg LEFT JOIN (SELECT customerId,totleAmount consumeMonthTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE weekMonthTypeId=2 AND tableName='CustomerRequestLogCustomerResponseLog' AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%m') ) hh ON gg.id=hh.customerId ) ii LEFT JOIN (SELECT customerId,sum(totleAmount) chargeTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE tableName='CustomerBalanceLog' GROUP BY customerId ) jj ON ii.id=jj.customerId ) kk LEFT JOIN (SELECT customerId,sum(totleAmount) consumeTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE tableName='CustomerRequestLogCustomerResponseLog' GROUP BY customerId ) ll ON kk.id=ll.customerId

SELECT id,reasonId,amount,customerId,createTime,reasonName FROM qydata.vwCustomerBalanceLog WHERE customerId=#{customerId}
AND reasonId IN
#{reasonId}
AND createTime>=str_to_date(#{beginDate},'%m/%d/%Y %H:%i:%s')
AND createTime<=str_to_date(#{endDate},'%m/%d/%Y %H:%i:%s')
ORDER BY createTime DESC
