mapper.select:
SELECT id,reasonId,amount,customerId,createTime,reasonName FROM qyfinance.bkvwCustomerBalanceLog WHERE customerId=#{customerId}
AND reasonId IN
#{reasonId}
AND createTime>=str_to_date(#{beginDate},'%Y/%m/%d %H:%i:%s')
AND createTime<=str_to_date(#{endDate},'%Y/%m/%d %H:%i:%s')

SELECT sum(amount) FROM qyfinance.bkvwCustomerBalanceLog WHERE customerId=#{customerId}
AND reasonId IN
#{reasonId}
AND createTime>=str_to_date(#{beginDate},'%Y/%m/%d %H:%i:%s')
AND createTime<=str_to_date(#{endDate},'%Y/%m/%d %H:%i:%s')

SELECT aa.apiTypeId,cc.name typeName,aa.apiId,aa.totlePrice,bb.vendorName,bb.vendorId
FROM
(
SELECT b.apiTypeId,b.apiId,sum(a.amount) totlePrice
FROM qyfinance.bkvwCustomerBalanceLog a LEFT JOIN qyfinance.bkvwCustomerRequestLog b ON a.reqId=b.id
WHERE a.reasonId IN(-1,-2,-3) AND b.hasCost=1 AND a.customerId=#{customerId}
GROUP BY b.apiTypeId,b.apiId
) aa
LEFT JOIN qyfinance.bkvwApi bb ON aa.apiId = bb.id
LEFT JOIN qyfinance.bkvwApiType cc ON aa.apiTypeId=cc.id


        aa.apiTypeId=#{apiTypeId}


        AND bb.vendorId=#{apiVendorId}

SELECT sum(aa.totlePrice)
FROM
(
SELECT b.apiTypeId,b.apiId,sum(a.amount) totlePrice
FROM qyfinance.bkvwCustomerBalanceLog a LEFT JOIN qyfinance.bkvwCustomerRequestLog b ON a.reqId=b.id
WHERE a.reasonId IN(-1,-2,-3) AND b.hasCost=1 AND a.customerId=#{customerId}
GROUP BY b.apiTypeId,b.apiId
) aa
LEFT JOIN qyfinance.bkvwApi bb ON aa.apiId = bb.id


        aa.apiTypeId=#{apiTypeId}


        AND bb.vendorId=#{apiVendorId}

SELECT DISTINCT(bb.vendorId) id,bb.vendorName name
FROM
(
SELECT b.apiTypeId,b.apiId
FROM qyfinance.bkvwCustomerBalanceLog a LEFT JOIN qyfinance.bkvwCustomerRequestLog b ON a.reqId=b.id
WHERE a.reasonId IN(-1,-2,-3) AND b.hasCost=1 AND a.customerId=#{customerId}
GROUP BY b.apiTypeId,b.apiId
) aa LEFT JOIN qyfinance.bkvwApi bb ON aa.apiId = bb.id
WHERE bb.vendorId IS NOT NULL

    AND aa.apiTypeId=#{apiTypeId}

SELECT aa.id,aa.reasonId,aa.amount,aa.customerId,aa.createTime,aa.reasonName,aa.apiTypeId,cc.name apiTypeName,bb.name mobileOperatorName, dd.vendorName FROM ( SELECT a.id,a.reasonId,a.amount,a.customerId,a.createTime,a.reasonName,b.apiTypeId,b.apiId FROM qyfinance.bkvwCustomerBalanceLog a LEFT JOIN qyfinance.bkvwCustomerRequestLog b ON a.reqId=b.id WHERE b.hasCost=1 AND a.customerId=#{customerId}
AND a.reasonId IN
#{reasonId}
) aa LEFT JOIN qyfinance.bkvwApiMobileOperator bb ON aa.apiId=bb.apiId LEFT JOIN qyfinance.bkvwApiType cc ON aa.apiTypeId=cc.id LEFT JOIN qyfinance.bkvwApi dd ON aa.apiId=dd.id
aa.apiTypeId=#{apiTypeId}
AND aa.createTime>=str_to_date(#{beginDate},'%Y/%m/%d %H:%i:%s')
AND aa.createTime<=str_to_date(#{endDate},'%Y/%m/%d %H:%i:%s')

SELECT sum(a.amount) FROM qyfinance.bkvwCustomerBalanceLog a LEFT JOIN qyfinance.bkvwCustomerRequestLog b ON a.reqId=b.id WHERE b.hasCost=1 AND a.customerId=#{customerId} AND b.apiTypeId=#{apiTypeId}
AND a.reasonId IN
#{reasonId}
AND a.createTime>=str_to_date(#{beginDate},'%Y/%m/%d %H:%i:%s')
AND a.createTime<=str_to_date(#{endDate},'%Y/%m/%d %H:%i:%s')

SELECT years,months,weeks,totleAmount,customerId,tableId,weekMonthTypeId,name,beginTime,endTime
FROM qyfinance.bkvwWeekMonthAmount
WHERE customerId=#{customerId} AND weekMonthTypeId=#{weekMonthTypeId}

    AND years=#{years}


    AND months=#{months}


    AND weeks=#{weeks}


    AND tableId IN

        #{tableId}

SELECT sum(totleAmount)
FROM qyfinance.bkvwWeekMonthAmount
WHERE customerId=#{customerId} AND weekMonthTypeId=#{weekMonthTypeId}

    AND years=#{years}


    AND months=#{months}


    AND weeks=#{weeks}


    AND tableId IN

        #{tableId}

