SELECT kk.id,kk.companyId,kk.companyName,kk.balance,kk.chargeWeekTotleAmount,kk.chargeMonthTotleAmount,kk.consumeWeekTotleAmount, kk.consumeMonthTotleAmount,kk.chargeTotleAmount,ll.consumeTotleAmount FROM (SELECT ii.id,ii.companyId,ii.companyName,ii.balance,ii.chargeWeekTotleAmount,ii.chargeMonthTotleAmount,ii.consumeWeekTotleAmount, ii.consumeMonthTotleAmount,jj.chargeTotleAmount FROM (SELECT gg.id,gg.companyId,gg.companyName,gg.balance,gg.chargeWeekTotleAmount,gg.chargeMonthTotleAmount,gg.consumeWeekTotleAmount, hh.consumeMonthTotleAmount FROM (SELECT ee.id,ee.companyId,ee.companyName,ee.balance,ee.chargeWeekTotleAmount,ee.chargeMonthTotleAmount,ff.consumeWeekTotleAmount FROM (SELECT cc.id,cc.companyId,cc.companyName,cc.balance,cc.chargeWeekTotleAmount,dd.chargeMonthTotleAmount FROM (SELECT aa.id,aa.typeId,aa.typeName,aa.name,aa.authId,aa.authPass,aa.createTime,aa.status,aa.statusName,aa.companyId,aa.companyName,aa.balance,bb.chargeWeekTotleAmount FROM (SELECT a.id,a.typeId,a.typeName,a.name,a.authId,a.authPass,a.balance,a.createTime,a.status,a.statusName,a.companyId,a.companyName, b.deptId,b.deptName FROM qydata.vwCustomer a,qyauth.vwCustomerDept b WHERE a.id=b.customerId
and b.deptId in
#{deptId}
and a.typeId in
#{customerTypeId}
and a.companyName like '%${content}%'
) aa LEFT JOIN (SELECT customerId,totleAmount chargeWeekTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE weekMonthTypeId=1 AND tableId=1 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%x') AND weeks=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%v') ) bb ON aa.id=bb.customerId ) cc LEFT JOIN (SELECT customerId,totleAmount chargeMonthTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE weekMonthTypeId=2 AND tableId=1 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%m') ) dd ON cc.id=dd.customerId ) ee LEFT JOIN (SELECT customerId,totleAmount consumeWeekTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE weekMonthTypeId=1 AND tableId=2 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%x') AND weeks=DATE_FORMAT(date_sub(sysdate(),interval 1 week),'%v') ) ff ON ee.id=ff.customerId ) gg LEFT JOIN (SELECT customerId,totleAmount consumeMonthTotleAmount FROM qyfinance.vwWeekMonthAmount WHERE weekMonthTypeId=2 AND tableId=2 AND years=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%Y') AND months=DATE_FORMAT(date_sub(sysdate(),interval 1 month),'%m') ) hh ON gg.id=hh.customerId ) ii LEFT JOIN (SELECT customerId,sum(amount) chargeTotleAmount FROM qydata.vwCustomerBalanceLog WHERE reasonId in (1,2,3) GROUP BY customerId ) jj ON ii.id=jj.customerId ) kk LEFT JOIN (SELECT customerId,sum(amount) consumeTotleAmount FROM qydata.vwCustomerBalanceLog WHERE reasonId in (-1,-2) GROUP BY customerId ) ll ON kk.id=ll.customerId

SELECT id,reasonId,amount,customerId,createTime,reasonName FROM qydata.vwCustomerBalanceLog WHERE customerId=#{customerId}
AND reasonId IN
#{reasonId}
AND createTime>=str_to_date(#{beginDate},'%m/%d/%Y %H:%i:%s')
AND createTime<=str_to_date(#{endDate},'%m/%d/%Y %H:%i:%s')
ORDER BY createTime DESC

select ee.apiTypeId,ff.name typeName,ee.apiId,ee.totlePrice,ee.vendorName,ee.vendorId from ( select cc.apiTypeId,cc.apiId,cc.totlePrice,dd.vendorName,dd.vendorId from ( select b.apiTypeId,b.apiId,sum(a.amount) totlePrice from qydata.vwCustomerBalanceLog a LEFT JOIN qydata.vwCustomerRequestLog b ON a.reqId=b.id where a.reasonId in(-1,-2) and b.hasCost=1 and a.customerId=#{customerId} group by b.apiTypeId,b.apiId ) cc LEFT JOIN qydata.vwApi dd ON cc.apiId = dd.id ) ee LEFT JOIN qydata.vwApiType ff ON ee.apiTypeId=ff.id
ee.apiTypeId=#{apiTypeId}
AND ee.vendorId=#{apiVendorId}

select ee.apiTypeId,ff.name typeName,ee.apiId,ee.reasonId,ee.reasonName,ee.totlePrice,ee.vendorName,ee.vendorId,ee.apiName from ( select cc.apiTypeId,cc.apiId,cc.reasonId,cc.reasonName,cc.totlePrice,dd.vendorName,dd.vendorId,dd.name apiName from ( select aa.apiTypeId,aa.apiId,aa.reasonId,bb.name reasonName,aa.totlePrice from ( select b.apiTypeId,b.apiId,a.reasonId,sum(a.amount) totlePrice from qydata.vwCustomerBalanceLog a LEFT JOIN qydata.vwCustomerRequestLog b ON a.reqId=b.id where a.reasonId in(-1,-2) and b.hasCost=1 and a.customerId=#{customerId} group by b.apiTypeId,b.apiId,a.reasonId ) aa LEFT JOIN qydata.CustomerBalanceModifyReason bb ON aa.reasonId=bb.id ) cc LEFT JOIN qydata.vwApi dd ON cc.apiId = dd.id ) ee LEFT JOIN qydata.vwApiType ff ON ee.apiTypeId=ff.id WHERE ee.apiTypeId=#{apiTypeId}
AND ee.reasonId IN
#{reasonId}

SELECT years,months,weeks,totleAmount,customerId,tableId,weekMonthTypeId,name,beginTime,endTime
FROM qyfinance.vwWeekMonthAmount
WHERE customerId=#{customerId}
AND weekMonthTypeId=#{weekMonthTypeId}

    AND years=#{years}


    AND months=#{months}


    AND weeks=#{weeks}


    AND tableId IN

        #{tableId}

