mapper.select:
SELECT a.customerId,a.totleConsume, b.floor,b.companyId, c.partnerId,c.name companyName,c.status, d.name partnerName FROM ( SELECT a.customerId,sum(a.totleConsume) totleConsume FROM ( SELECT a.customerId,a.cost * sum(a.amount) totleConsume FROM qyfinance.CustomerHistoryBill a
AND a.yearMonth IN
#{beg}
GROUP BY a.customerId,a.cost ) a GROUP BY a.customerId ) a LEFT JOIN qyfinance.finance_Customer b ON a.customerId = b.id LEFT JOIN qyfinance.bkvwCustomerCompany c ON b.companyId = c.id LEFT JOIN qyfinance.bkvwPartner d ON c.partnerId = d.id WHERE b.typeId = 1 AND b.marketId IS NULL
AND c.status IN
#{status}
AND b.id IN
#{cid}
AND c.partnerId = #{pid}
AND c.partnerId IS NULL

SELECT a.id,a.year,a.month,a.yearMonth,a.customerId,a.apiTypeId,a.stid,a.cost,a.amount,a.isLock,
b.name apiTypeName,c.name stidName
FROM qyfinance.CustomerHistoryBill a
LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId = b.id
LEFT JOIN qyfinance.bkvwMobileOperator c ON a.stid = c.id
WHERE a.customerId = #{cid}

    AND a.yearMonth IN

        #{con}



    AND a.apiTypeId IN

        #{tid}



    AND a.stid IN

        #{stid}

mapper.update:
UPDATE qyfinance.CustomerHistoryBill
SET cost = #{param2}
WHERE id = #{param1}

UPDATE qyfinance.CustomerHistoryBill
SET amount = #{param2}
WHERE id = #{param1}

mapper.delete:
DELETE FROM qyfinance.CustomerHistoryBill
WHERE id IN

    #{id}

mapper.select:
SELECT a.yearMonth,sum(a.amount) amount
FROM qyfinance.CustomerHistoryBill a
WHERE a.customerId = #{cid}
AND a.yearMonth IN

    #{yearMonth}

GROUP BY a.yearMonth

SELECT a.customerId,a.yearMonth,a.apiTypeId,a.stid,a.amount,CONCAT_WS('--',b.name,c.name) apiTypeName
FROM qyfinance.CustomerHistoryBill a
LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId = b.id
LEFT JOIN qyfinance.bkvwMobileOperator c ON a.stid = c.id
WHERE a.customerId = #{cid}
AND a.yearMonth = #{yearMonth}

SELECT companyId
FROM qyfinance.finance_Customer a
WHERE a.id = #{cid}

mapper.update:
UPDATE qyfinance.CustomerHistoryBill
SET isLock = #{isLock}
WHERE id IN

    #{id}

mapper.select:
SELECT a.isLock
FROM qyfinance.CustomerHistoryBill a
WHERE a.id = #{param}

SELECT a.beforData,a.afterData,a.content,a.typeId,b.name typeName,a.createTime
FROM qyfinance.CustomerHistoryBillUpdateLog a
LEFT JOIN qyfinance.CustomerHistoryBillUpdateLogType b ON a.typeId = b.id
WHERE a.customerHistoryBillId = #{id}

    AND a.typeId = #{typeId}
