mapper.select:
select c.id,c.typeId,c.name,c.authId,c.authPass,c.balance,c.createTime,c.qctname,c.status,c.qcsname, d.deptName,c.companyName from qyauth.CustomerDept cda,qyauth.Dept d,( select customer.id,customer.typeId,customer.name,customer.authId,customer.authPass,customer.balance, customer.createTime,customer.status,qct.name qctname,qcs.name qcsname,company.name companyName from qydata.Customer customer,qydata.CustomerType qct,qydata.CustomerStatus qcs,qydata.CustomerCompany company where customer.typeId=qct.id and customer.status=qcs.id and customer.companyId=company.id ) c where cda.customerId=c.id and cda.deptId=d.id
and cda.deptId in
#{deptId}
and c.companyName like '%${content}%'
and c.typeId in
#{customerTypeId}
order by c.createTime desc limit #{beginIndex},#{lineSize}

select count(c.id)
from qyauth.CustomerDept cda,qyauth.Dept d,(
                            select customer.id,customer.typeId,customer.name,customer.authId,customer.authPass,customer.balance,
                            customer.createTime,customer.status,qct.name qctname,qcs.name qcsname,company.name companyName
                            from qydata.Customer customer,qydata.CustomerType qct,qydata.CustomerStatus qcs,qydata.CustomerCompany company
                            where customer.typeId=qct.id and customer.status=qcs.id and customer.companyId=company.id
                          ) c
where cda.customerId=c.id and cda.deptId=d.id

    and cda.deptId in

        #{deptId}



    and c.companyName like '%${content}%'


    and c.typeId in

        #{customerTypeId}
