AND b.createTime >= #{beginDate}
AND b.createTime < #{endDate}
AND a.customerId NOT IN
#{cid}
AND c.partnerId = #{pid}
) a LEFT JOIN qyfinance.bkvw_api_ApiTypeVendorPartner b ON a.apiId = b.apiId WHERE b.partnerId IS NULL GROUP BY a.apiId ) a LEFT JOIN qyfinance.bkvw_api_ApiTypeVendorPartner b ON a.apiId = b.apiId LEFT JOIN qyfinance.excel_ApiPrice c ON a.apiId = c.apiId GROUP BY b.apiTypeId,b.vendorId,b.mobileOperatorId,c.cost ) a LEFT JOIN qyfinance.bkvwApiType b ON a.apiTypeId = b.id LEFT JOIN qyfinance.finance_ApiVendor c ON a.vendorId = c.id LEFT JOIN qyfinance.bkvwMobileOperator d ON a.mobileOperatorId = d.id WHERE a.sumCount > 0

