mapper.select:
SELECT a.apiTypeId,a.stid,a.customerId,a.reqId,a.k,a.content reqContent,a.createTime, b.content respContent,b.apiId,b.dataSourceId,b.c0cacheId,b.dur,c.amount, CONCAT_WS('--',d.name,e.name) type_stid_name, CONCAT_WS('--',g.name,h.name,f.authId) cname_pname_authId, CONCAT_WS('--',i.vendorName,i.partnerName,i.apiName) vname_pname_aname FROM qydata.CustomerRequestLog a LEFT JOIN qydata.CustomerResponseLog b ON a.id = b.reqId LEFT JOIN qydata.CustomerBalanceLog c ON a.id = c.reqId LEFT JOIN qyfinance.bkvwApiType d ON a.apiTypeId = d.id LEFT JOIN qyfinance.bkvwMobileOperator e ON a.stid = e.id LEFT JOIN qyfinance.finance_Customer f ON a.customerId = f.id LEFT JOIN qyfinance.bkvwCustomerCompany g ON f.companyId = g.id LEFT JOIN qyfinance.bkvwPartner h ON g.partnerId = h.id LEFT JOIN qyfinance.bkvw_api_ApiTypeVendorPartner i ON b.apiId = i.apiId
AND a.${k_reqId} = #{content}
AND a.customerId = #{cid}
