mapper.select:
SELECT a.vendorId,a.consumeAmount,b.name vendorName,d.partnerId,d.partnerName,d.status FROM ( SELECT a.vendorId,sum(a.consumeAmount) consumeAmount FROM ( SELECT a.vendorId,a.cost * sum(a.amount) consumeAmount FROM qyfinance.VendorHistoryBill a
AND a.yearMonth IN
#{beg}
GROUP BY a.vendorId,a.cost ) a WHERE a.vendorId IS NOT NULL GROUP BY a.vendorId ) a LEFT JOIN qyfinance.finance_ApiVendor b ON a.vendorId = b.id LEFT JOIN qyfinance.ApiVendorBalance c ON a.vendorId = c.vendorId LEFT JOIN qyfinance.bkvwApiVendor d ON a.vendorId = d.vendorId
AND d.status IN
#{status}
AND a.vendorId IN
#{vid}
AND d.partnerId = #{pid}
AND d.partnerId IS NULL

SELECT a.vendorId,SUM(a.amount) chargeAmount FROM qyfinance.ApiVendorBalanceLog a
WHERE a.createTime < #{currMonthTime}
GROUP BY a.vendorId

SELECT a.id,a.year,a.month,a.yearMonth,a.vendorId,a.apiId,b.apiTypeId,c.mobileOperatorId stid,
a.cost,a.amount,d.name apiTypeName,e.name stidName,a.isLock
FROM qyfinance.VendorHistoryBill a
LEFT JOIN qyfinance.finance_Api b ON a.apiId = b.id
LEFT JOIN qyfinance.bkvwApiMobileOperator c ON a.apiId = c.apiId
LEFT JOIN qyfinance.bkvwApiType d ON b.apiTypeId = d.id
LEFT JOIN qyfinance.bkvwMobileOperator e ON c.mobileOperatorId = e.id
WHERE a.vendorId = #{vid}

    AND a.yearMonth IN

        #{cyc}



    AND a.apiId IN

        #{aid}



    AND a.isLock = #{isLock}

SELECT a.isLock
FROM qyfinance.VendorHistoryBill a
WHERE a.id = #{param}

SELECT a.beforData,a.afterData,a.content,a.type,b.name typeName,a.createTime
FROM qyfinance.VendorHistroyBillUpdateLog a
LEFT JOIN qyfinance.VendorHistoryBillUpdateLogType b ON a.type = b.id
WHERE a.vendorHistoryBillId = #{id}

    AND a.type = #{typeId}
